import React from 'react';
import Header from './header';
import Hero from './hero';
import ReferralSection from './ReferralSection';
import ReferralBenefits from './ReferralBenefits';
import Footer from './Footer';

function App() {
  const [showPopup, setShowPopup] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { register, handleSubmit, formState: { errors }, reset } = useForm();

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  const onSubmit = async (data) => {
    setIsSubmitting(true);
    try {
      console.log(data);
      
      await axios.post('http://localhost:5000/submit-referral', data);
      alert('Form submitted successfully!');
      reset(); // Clear form fields after submission
      setShowPopup(false); // Close form popup after submission
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Failed to submit form. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };
  return (
    <div>
      <button className="bg-blue-500 text-white py-2 px-6 rounded-lg" onClick={togglePopup}>Refer Now</button>
      <Header />
      <Hero />
      <ReferralSection />
      <ReferralBenefits />
      <Footer />
    </div>
  );
}

export default App;
